mapper.select:
SELECT COUNT(ap.id)
FROM asset_port ap
WHERE ap.ip = #{ipId};

select ai.*, count(ap.id) as portCount
from asset_ip ai
         left join asset_ip_sub_domain ais on ai.id = ais.ip_id
         left join asset_port ap on ai.id = ap.ip
where ais.subdomain_id = #{id}
group by ai.id;

select *
from asset_ip
where ip = #{ip} AND project_id = #{projectId};
